HelperJS=function(a){this.bIsOverlayOpen=!1;var b=this;$("#"+a).bind("click",function(){b.addOverlay()});$(window).resize(function(){b.bIsOverlayOpen&&(b.removeOverlay(),b.addOverlay())})};HelperJS.prototype.addOverlay=function(){var a=$(document).find("[helper-bind]");this.bIsOverlayOpen=!0;this.eLeftElement=this._createLeftElement(a);this.eTopElement=this._createTopElement(a);this.eRightElement=this._createRightElement(a);this.eBottomElement=this._createBottomElement(a)};
HelperJS.prototype.removeOverlay=function(){this.bIsOverlayOpen=!1;this.eLeftElement.remove();this.eTopElement.remove();this.eRightElement.remove();this.eBottomElement.remove()};HelperJS.prototype._createLeftElement=function(a){a=a.position().left;var b=$(window).height();return this._createOverlayElement(b,a,0,0)};HelperJS.prototype._createTopElement=function(a){var b=a.position().top,c=a.position().left;return this._createOverlayElement(b,a.width(),0,c)};
HelperJS.prototype._createRightElement=function(a){a.position();var b=a.position().left,c=$(window).width(),e=$(window).height(),c=c-(b+a.width());a=b+a.width();return this._createOverlayElement(e,c,0,a)};HelperJS.prototype._createBottomElement=function(a){var b=a.position().top,c=a.position().left,e=$(window).height(),b=b+a.height();return this._createOverlayElement(e-b,a.width(),b,c)};
HelperJS.prototype._createOverlayElement=function(a,b,c,e){var d=$("<div></div>");d.css("height",a+"px");d.css("width",b+"px");d.offset({top:c,left:e});d=this._addOverlayStyling(d);$("body").append(d);var f=this;d.bind("click",function(){f.removeOverlay()});return d};HelperJS.prototype._addOverlayStyling=function(a){a.css("opacity","0.8");a.css("background-color","#CCC");a.css("position","fixed");a.css("z-index","9999999");return a};