HelperJS=function(e){this.bIsOverlayOpen=false;var t=this;$("#"+e).bind("click",function(){t.addOverlay()});$(window).resize(function(){if(t.bIsOverlayOpen){t.removeOverlay();t.addOverlay()}})};HelperJS.prototype.addOverlay=function(){var e=$(document).find("[helper-bind]");this.bIsOverlayOpen=true;this.eLeftElement=this._createLeftElement(e);this.eTopElement=this._createTopElement(e);this.eRightElement=this._createRightElement(e);this.eBottomElement=this._createBottomElement(e)};HelperJS.prototype.removeOverlay=function(){this.bIsOverlayOpen=false;this.eLeftElement.remove();this.eTopElement.remove();this.eRightElement.remove();this.eBottomElement.remove()};HelperJS.prototype._createLeftElement=function(e){var t=e.position().left;var n=$(window).height();return this._createOverlayElement(n,t,0,0)};HelperJS.prototype._createTopElement=function(e){var t=e.position().top;var n=e.position().left;return this._createOverlayElement(t,e.width(),0,n)};HelperJS.prototype._createRightElement=function(e){var t=e.position().top;var n=e.position().left;var r=$(window).width();var i=$(window).height();var s=r-(n+e.width());var o=n+e.width();return this._createOverlayElement(i,s,0,o)};HelperJS.prototype._createBottomElement=function(e){var t=e.position().top;var n=e.position().left;var r=$(window).height();var i=t+e.height();var s=r-i;return this._createOverlayElement(s,e.width(),i,n)};HelperJS.prototype._createOverlayElement=function(e,t,n,r){var i=$("<div class='overlay-body'></div>");i.css("height",e+"px");i.css("width",t+"px");i.offset({top:n,left:r});$("body").append(i);var s=this;i.bind("click",function(){s.removeOverlay()});return i}