HelperJS=function(a){this.bIsOverlayOpen=!1;var c=this;$("#"+a).bind("click",function(){c.addOverlay()});$(window).resize(function(){c.bIsOverlayOpen&&(c.removeOverlay(),c.addOverlay())})};
HelperJS.prototype.addOverlay=function(){var a=$(document).find("[helper-bind]");this.bIsOverlayOpen=!0;this.eLeftElement=this._createLeftElement(a);this.eTopElement=this._createTopElement(a);this.eRightElement=this._createRightElement(a);this.eBottomElement=this._createBottomElement(a);var c=$(a).find("[helper-description]");this.eHelpDescription=this._createDescription(a,c.attr("helper-description"));this.eLine=this._createLine(a[0].offsetLeft+a.width(),a[0].offsetTop+a.height()/2,this.eHelpDescription[0].offsetLeft+
this.eHelpDescription.width()/2,this.eHelpDescription[0].offsetTop)};HelperJS.prototype.removeOverlay=function(){this.bIsOverlayOpen=!1;this.eLeftElement.remove();this.eTopElement.remove();this.eRightElement.remove();this.eBottomElement.remove();this.eHelpDescription.remove();this.eLine.remove()};HelperJS.prototype._createLeftElement=function(a){a=a[0].offsetLeft;var c=$(window).height();return this._createOverlayElement(c,a,0,0)};
HelperJS.prototype._createTopElement=function(a){var c=a[0].offsetLeft;return this._createOverlayElement(a[0].offsetTop,a.width(),0,c)};HelperJS.prototype._createRightElement=function(a){var c=a[0].offsetLeft,d=$(window).width(),e=$(window).height(),d=d-(c+a.width());a=c+a.width();return this._createOverlayElement(e,d,0,a)};
