HelperJS.prototype._createDescription=function(a,c){var d=a[0].offsetTop,e=a[0].offsetLeft+a.width(),b=$("<div>"+c+"</div>");b.offset({top:d+75,left:e+100});b.css("position","fixed");b.css("z-index","9999999");b.css("border","1px solid #FFF");b.css("border-bottom-style","none");b.css("border-left-style","none");b.css("border-right-style","none");b.css("color","#FFF");b.css("padding-top","5px");$("body").append(b);return b};
HelperJS.prototype._addOverlayStyling=function(a){a.css("opacity","0.8");a.css("background-color","#444");a.css("position","fixed");a.css("z-index","9999998");return a};HelperJS.prototype._addLineStyling=function(a){a.css("border","1px solid #FFF");a.css("border-bottom-style","none");a.css("border-left-style","none");a.css("position","fixed");a.css("z-index","9999998");return a};
HelperJS.prototype._createLine=function(a,c,d,e){e-=c;d-=a;var b=$("<div></div>");b.css("height",e+"px");b.css("width",d+"px");b.offset({top:c,left:a});b=this._addLineStyling(b);$("body").append(b);var f=this;b.bind("click",function(){f.removeOverlay()});return b};