HelperJS=function(a){this.bIsOverlayOpen=!1;var b=this;$("#"+a).bind("click",function(){b.addOverlay()});$(window).resize(function(){b.bIsOverlayOpen&&(b.removeOverlay(),b.addOverlay())})};HelperJS.prototype.addOverlay=function(){var a=$(document).find("[helper-bind]");this.bIsOverlayOpen=!0;this.eLeftElement=this._createLeftElement(a);this.eTopElement=this._createTopElement(a);this.eRightElement=this._createRightElement(a);this.eBottomElement=this._createBottomElement(a)};
HelperJS.prototype.removeOverlay=function(){this.bIsOverlayOpen=!1;this.eLeftElement.remove();this.eTopElement.remove();this.eRightElement.remove();this.eBottomElement.remove()};HelperJS.prototype._createLeftElement=function(a){a=a[0].offsetLeft;var b=$(window).height();return this._createOverlayElement(b,a,0,0)};HelperJS.prototype._createTopElement=function(a){var b=a[0].offsetLeft;return this._createOverlayElement(a[0].offsetTop,a.width(),0,b)};
HelperJS.prototype._createRightElement=function(a){var b=a[0].offsetLeft,d=$(window).width(),e=$(window).height(),d=d-(b+a.width());a=b+a.width();return this._createOverlayElement(e,d,0,a)};HelperJS.prototype._createBottomElement=function(a){var b=a[0].offsetTop,d=a[0].offsetLeft,e=$(window).height(),b=b+a.height();return this._createOverlayElement(e-b,a.width(),b,d)};
HelperJS.prototype._createOverlayElement=function(a,b,d,e){var c=$("<div></div>");c.css("height",a+"px");c.css("width",b+"px");c.offset({top:d,left:e});c=this._addOverlayStyling(c);$("body").append(c);var f=this;c.bind("click",function(){f.removeOverlay()});return c};HelperJS.prototype._addOverlayStyling=function(a){a.css("opacity","0.8");a.css("background-color","#CCC");a.css("position","fixed");a.css("z-index","9999999");return a};